mapper.select:
SELECT A.id , A.tracker_id , TRK.name AS tracker_name , A.project_id , PRJ.name AS project_name , A.subject , A.description , A.fixed_version_id , VER.name AS fixed_version_name , A.status_id , STA.name AS status_name , A.author_id , ATH.lastname , ATH.firstname , ATH.login , A.assigned_to_id , ASS.lastname , ASS.firstname , ASS.login , A.priority_id , PRI.name AS priority_name , A.done_ratio , A.start_date , A.due_date FROM issues A INNER JOIN trackers TRK ON TRK.id = A.tracker_id INNER JOIN projects PRJ ON PRJ.id = A.project_id INNER JOIN versions VER ON VER.id = A.fixed_version_id INNER JOIN issue_statuses STA ON STA.id = A.status_id INNER JOIN enumerations PRI ON PRI.id = A.priority_id AND PRI.type = 'issuePriority' INNER JOIN users ATH ON ATH.id = A.author_id LEFT JOIN users ASS ON ASS.id = A.assigned_to_id
AND A.fixed_version_id = #{fixedVersionId} AND A.project_id = #{projectId} AND A.author_id = #{authorId}
ORDER BY A.id DESC
